import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [users, setUsers] = useState([]);
  const [posts, setPosts] = useState([]);
  const [serverInfo, setServerInfo] = useState(null);
  const [newUserName, setNewUserName] = useState('');
  const [newUserEmail, setNewUserEmail] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch data from backend
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [usersRes, postsRes, infoRes] = await Promise.all([
        axios.get('/api/users'),
        axios.get('/api/posts'),
        axios.get('/api/server-info')
      ]);
      
      setUsers(usersRes.data);
      setPosts(postsRes.data);
      setServerInfo(infoRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const addUser = async (e) => {
    e.preventDefault();
    if (!newUserName || !newUserEmail) return;

    try {
      const response = await axios.post('/api/users', {
        name: newUserName,
        email: newUserEmail
      });
      
      setUsers([...users, response.data]);
      setNewUserName('');
      setNewUserEmail('');
    } catch (error) {
      console.error('Error adding user:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="App">
      <header className="app-header">
        <h1>React + Node.js Website</h1>
        {serverInfo && (
          <div className="server-info">
            <p>Server: {serverInfo.server} | Status: {serverInfo.status}</p>
            <p>Last updated: {new Date(serverInfo.timestamp).toLocaleString()}</p>
          </div>
        )}
      </header>

      <main className="main-content">
        {/* Add User Form */}
        <section className="section">
          <h2>Add New User</h2>
          <form onSubmit={addUser} className="user-form">
            <input
              type="text"
              placeholder="Name"
              value={newUserName}
              onChange={(e) => setNewUserName(e.target.value)}
              required
            />
            <input
              type="email"
              placeholder="Email"
              value={newUserEmail}
              onChange={(e) => setNewUserEmail(e.target.value)}
              required
            />
            <button type="submit">Add User</button>
          </form>
        </section>

        {/* Users List */}
        <section className="section">
          <h2>Users ({users.length})</h2>
          <div className="cards-container">
            {users.map(user => (
              <div key={user.id} className="card">
                <h3>{user.name}</h3>
                <p>{user.email}</p>
                <span className="badge">ID: {user.id}</span>
              </div>
            ))}
          </div>
        </section>

        {/* Posts List */}
        <section className="section">
          <h2>Latest Posts ({posts.length})</h2>
          <div className="posts-container">
            {posts.map(post => (
              <div key={post.id} className="post-card">
                <h3>{post.title}</h3>
                <p>{post.content}</p>
                <div className="post-meta">By {post.author}</div>
              </div>
            ))}
          </div>
        </section>
      </main>

      <footer className="app-footer">
        <p>Built with React.js and Node.js Express</p>
      </footer>
    </div>
  );
}

export default App;
